import React from 'react';

const seasonConfig = {
    summer: {
        text: "Let's hit the beach",
        iconName: "sun"
    },
    winter: {
        text: "Burr, it's chilly",
        iconName: "snowflake"
    }
}

function getSeason(latitude, month) {
    if (month>2 && month<9) {
       return latitude > 0 ? "summer" : "winter";
    } else {
        return latitude < 0 ? "summer" : "winter";
    }
}

const SeasonDisplay = ({ latitude }) => {
    const season = getSeason(latitude, new Date().getMonth());
    return (
        <div>
            <i className={`icon ${seasonConfig[season].iconName}`}></i>
            <h1>{season === "winter" ? "" : ""}</h1>
            <i className={`icon ${season === "winter" ? "snowflake" : "sun"}`}></i>
        </div>
    )
}


export default SeasonDisplay;
