//     dispatch({
//         type: "FETCH_USER",
//         payload: response.data
//     })
// }

export const fetchUser = function(userid) {
    return async function(dispatch) {
        const response = await jsonPlaceholder.get(`/users/${userid}`);
        dispatch({
            type: "FETCH_USER",
            payload: response.data
        })
    }
}
