import React from 'react';
import {render} from 'react-dom';
// import Comment from "./Comment";
// import ApprovalCard from "./ApprovalCard";
// import faker from "faker";


class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            latitude: null,
            errorMsg: ""
        }
    }
    componentDidMount(){
        window.navigator.geolocation.getCurrentPosition(
            position => this.setState({latitude: position.coords.latitude}),
            err => this.setState({errorMsg: err.message})
        );
    }
    render() {
        if(this.state.errorMsg && !this.state.latitude) {
            return (
                <p>Error: {this.state.errorMsg}</p>
            )
        } 
        if (!this.state.errorMsg && this.state.latitude) {
            return (
                <p>Latitude {this.state.latitude}</p>
            )
        } 
        return (
            <p>Loading...</p>
        )

    }

    // return (
    //     <div className="ui container comments">
    //         <ApprovalCard>
    //             <Comment name="Mila" text="Great Content!" avatar={faker.image.avatar()}/>
    //         </ApprovalCard>
    //         <ApprovalCard>
    //             <Comment name="Alex" text="Interesting" avatar={faker.image.avatar()}/>
    //         </ApprovalCard>
    //         <ApprovalCard>
    //             <Comment name="Tom" text="Like it" avatar={faker.image.avatar()}/>
    //         </ApprovalCard>
    //         </div>
    // )
};



render(<App />, document.getElementById('root'));
