import React from 'react';
import axios from 'axios';
import SearchBar from './SearchBar';


class App extends React.Component {
    state = {
        images: []
    }
    async onFormSubmit(term) {
        const response = await axios.get('https://api.unsplash.com/search/photos', {
            params: {
                query: term
            },
            headers: {
                Authorization: 'Client-ID kwBc01HChEalu54iAH4DcT8JdoKigfPfp-ZitBo6SfY'
            }
        });
        this.setState({images: response.data.results.length});
    }
    render() {
        return (
            <div className="ui container" style={{marginTop: '10px'}}><SearchBar myOnSubmit={this.onFormSubmit}/></div>
        )

    }
};

export default App;
